import React from 'react';
import './styles.css';
import { HashRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import Home from './Home';
import About from './About';
import Access from './Access';
import AboutFont from './AboutFont';
import News from './News';
import Works from './Works';
import NewsDetail from './NewsDetail';
import WorksDetail from './WorksDetail';
import Footer from './components/Footer';
import Header from './components/Header';
import { useNavigate } from 'react-router-dom';

function useScrollToTop() {
  const { pathname } = useLocation();

  React.useEffect(() => {
    const handleScrollToTop = (event) => {
      // クリックした要素が <a> 要素（リンク）であればスクロール
      if (event.target.tagName === 'A') {
        window.scrollTo(0, 0);
      }
    };

    // ページ遷移やリンククリック時にスクロールをトリガー
    window.scrollTo(0, 0);

    // クリックイベントを監視してリンククリック時にトップへスクロール
    window.addEventListener('click', handleScrollToTop);

    // クリーンアップ関数でイベントリスナーを削除
    return () => {
      window.removeEventListener('click', handleScrollToTop);
    };
  }, [pathname]);  // `pathname` が変わるたびに再実行

  return null;  // UI は返さない
}


const useLanguage = () => {
  const location = useLocation();
  return location.pathname.startsWith('/en'); // /enが含まれているなら英語
};

function App() {
  useScrollToTop();  // ページ遷移時にトップへスクロール
  const isEnglish=useLanguage()
  return (
    <div>
      <Header />
      <div className='maincontent'>
      <Routes>
        {/* 日本語ページ */}
        <Route path="/" element={<Home isEnglish={isEnglish} />} />
        <Route path="/about" element={<About isEnglish={isEnglish} />} />
        <Route path="/access" element={<Access isEnglish={isEnglish} />} />
        <Route path="/aboutfont" element={<AboutFont isEnglish={isEnglish} />} />
        <Route path="/news" element={<News isEnglish={isEnglish} />} />
        <Route path="/works" element={<Works isEnglish={isEnglish} />} />
        <Route path="/news/:id" element={<NewsDetail isEnglish={isEnglish} />} />
        <Route path="/works/:id" element={<WorksDetail isEnglish={isEnglish} />} />

        {/* 英語ページ */}
        <Route path="/en" element={<Home isEnglish={isEnglish} />} />
        <Route path="/en/about" element={<About isEnglish={isEnglish} />} />
        <Route path="/en/access" element={<Access isEnglish={isEnglish} />} />
        <Route path="/en/aboutfont" element={<AboutFont isEnglish={isEnglish} />} />
        <Route path="/en/news" element={<News isEnglish={isEnglish} />} />
        <Route path="/en/works" element={<Works isEnglish={isEnglish} />} />
        <Route path="/en/news/:id" element={<NewsDetail isEnglish={isEnglish} />} />
        <Route path="/en/works/:id" element={<WorksDetail isEnglish={isEnglish} />} />
      </Routes>
      </div>
      <Footer />
    </div>
  );
}

export default function MainApp() {
  return (
    <Router>  {/* App全体をRouterでラップ */}
      <App />
    </Router>
  );
}
